$(document).ready(function(){$("#testimonials ul li").click(function(i){i.preventDefault();var t=$(this).attr("id").substring(2,1),a=$("#t"+t),s=$("#testimonial-"+t),d=[];d=$(".words div").toArray();var e=[];e=$("#testimonials ul li").toArray(),$(e).each(function(){$(this).find("a div img").hasClass("show")&&($(this).attr("id")==a.attr("id")||$(this).find("a div img").fadeTo(200,0).removeClass("show"))}),$(d).each(function(){$(this).hasClass("open")&&($(this).attr("id")==s.attr("id")||$(this).hide().removeClass("open"))}),a.find("a div img").fadeTo(200,1).addClass("show"),s.fadeIn(200).addClass("open")})});